<script setup>
import { components } from "~/slices";
const { client } = usePrismic();

//METADATA
const { data: metadata } = await useAsyncData("metadata", () =>
  client.getSingle("metadata")
);

// GET SINGLE HOME
const { data: home } = await useAsyncData("index", () =>
  client.getSingle("home")
);

</script>

<script>
import gsap from "gsap";
import ScrollTrigger from "gsap/ScrollTrigger";
import SplitText from "gsap/SplitText";
export default {
  data() {
    return {
      sections: [],
    };
  },
  mounted() {
    gsap.registerPlugin(ScrollTrigger, SplitText);

    this.scroll();
  },
  methods: {
    setRef(el) {
      if (el) this.sections.push(el);
    },
    scroll() {
      this.sections.forEach((el) => {
        const titles = gsap.utils.toArray("[data-title]", el);
        const subtitles = gsap.utils.toArray("[data-subtitle]", el);
        const texts = gsap.utils.toArray("[data-text]", el);
        const images = gsap.utils.toArray("[data-image]", el);

        if (titles) {
          titles.forEach((title, index) => {
            const duration = parseInt(title.dataset.duration) || 1.25;
            const scrub = false || title.dataset.scrub;

            const split = new SplitText(title, {
              type: "lines, words",
              linesClass: "overflow-hidden",
            });

            gsap.fromTo(
              split.words,
              duration,
              {
                yPercent: 150,
                opacity: 0,
              },
              {
                ease: "expo.out",
                yPercent: 0,
                opacity: 1,
                stagger: 0.075,
                scrollTrigger: {
                  trigger: el,
                  scrub: scrub,
                  start: "top 30%",
                  markers: false,
                },
                onComplete: () => {
                  split.revert();
                },
              }
            );
          });
        }

        if (subtitles) {
          subtitles.forEach((title, index) => {
            const duration = parseInt(title.dataset.duration) || 1.25;
            const scrub = false || title.dataset.scrub;

            const split = new SplitText(title, {
              type: "lines, words",
              linesClass: "overflow-hidden",
            });

            gsap.fromTo(
              split.words,
              duration,
              {
                opacity: 0,
              },
              {
                ease: "expo.out",
                opacity: 1,
                delay: 0.5,
                stagger: 0.075,
                scrollTrigger: {
                  trigger: el,
                  scrub: scrub,
                  start: "top 30%",
                  markers: false,
                },
                onComplete: () => {
                  split.revert();
                },
              }
            );
          });
        }

        if (texts) {
          texts.forEach((text, index) => {
            const scrub = false || text.dataset.scrub;
            gsap.fromTo(
              text,
              {
                opacity: 0,
                yPercent: 150,
              },
              {
                opacity: 1,
                yPercent: 0,
                scrollTrigger: {
                  trigger: el,
                  scrub: scrub,
                  start: "top 30%",
                  markers: false,
                },
              }
            );
          });
        }

        if (images) {
          images.forEach((image, index) => {
            gsap.fromTo(
              image,
              {
                opacity: 0,
                yPercent: 150,
              },
              {
                opacity: 1,
                yPercent: 0,
                scrollTrigger: {
                  trigger: el,
                  scrub: false,
                  start: "top 70%",
                  end: "bottom 100%",
                  markers: false,
                },
              }
            );
          });
        }
      });
    },
  },
};
</script>

